/* Categoria*/

#categoria h2 {
  text-align: right;
}

#categoria main {
  width: 95%;
  display: flex;
  flex-direction: column;
  margin: 0 auto;
}

#categoria main section {
  width: 90%;
  margin: 0 auto;
  margin-top: 4%;
}

#categoria main section ul {
  list-style: none;
  padding: 0;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
}

#categoria main section ul li {
  width: 300px;
  /*border: 1px solid red;*/
  margin-top: 2%;
}
#categoria main section ul li img {
  width: 300px;
  height: 300px;
  border-radius: 100%;
  margin: 0 auto;
  /*border: 1px solid red;*/
}

#categoria main section ul li a {
  display: flex;
  flex-direction: column;
  text-decoration: none;
}
.name_categoria {
  margin: 0 auto;
  margin-top: 5%;
  margin-bottom: 10%;
  color: var(--blue-brazil);
}
.name_categoria div {
  font-size: 1.2rem;
}

/* Detalle */

#mandioca main {
  width: 95%;
  margin: 0 auto;
  text-align: justify;
}

#mandioca main p {
  width: 95%;
  margin: 0 auto;
  margin-top: 6%;
}
#mandioca main h3 {
  margin-bottom: 5%;
  margin-top: 11%;
}

#mandioca main h2 {
  text-align: right;
}

#mandioca main li {
  margin-bottom: 5%;
  background-size: 100px;
}

#mandioca iframe {
  width: 100%;
  margin-bottom: 7%;
}

#mandioca section:last-child img {
  width: 100px;
  height: 100px;
  border-radius: 100%;
  align-self: center;
}
#mandioca section:last-child div div {
  display: flex;
  float: right;
  flex-direction: column;
  text-align: center;
  text-indent: 0;
}
#mandioca section:last-child div {
  margin-bottom: 3%;
}

#mandioca section:last-child div div p {
  text-indent: 0;
}

#mandioca section:last-child div div p:first-child {
  font-weight: bold;
  text-transform: uppercase;
}

/* Presentacion */

#presentacion img {
  width: 50%;
}

#presentacion main h2 {
  text-align: right;
  margin-bottom: 5%;
}

#presentacion main {
  width: 95%;
  margin: 0 auto;
  text-align: justify;
}
#presentacion main p {
  padding-bottom: 3%;
}
#presentacion div p:first-child {
  text-align: center;
  padding-bottom: 4%;
  font-size: 2rem;
  color: var(--green-brazil);
}
#presentacion main img {
  display: block;
  margin: auto;
  padding-bottom: 6%;
}

/* Enlaces */

#enlaces main h2 {
  text-align: right;
  margin-bottom: 5%;
}
#enlaces main {
  width: 95%;
  margin: 0 auto;
  text-align: justify;
}

#enlaces main li {
  padding-bottom: 1%;
}

/*            *
 *   Tablet   *
 *            */

@media screen and (min-width: 600px) and (max-width: 1024px) {
  * {
    font-size: 16px;
  }
  /* Header */
  header div {
    width: 40%;
  }

  /*Footer*/
  footer {
    margin-top: 8%;
  }

  /*Portada*/
  #portada main {
    margin-top: 7%;
  }
  #portada main p {
    font-size: 6rem;
  }
  /*  Detalle */

  #mandioca main p {
    margin-top: 2%;
  }

  #mandioca main h3 {
    margin-bottom: 2%;
    margin-top: 7%;
  }
}

/*                 *
 *   Escritorio    *
 *                 */

@media screen and (min-width: 1025px) {
  * {
    font-size: 16px;
  }

  /* Header */

  header {
    margin-top: 0%;
    padding: 1%;
    flex-direction: row;
    justify-content: space-between;
  }

  header div {
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    width: 20%;
  }

  nav {
    width: 20%;
    margin: 0;
    padding-right: 1%;
  }

  /*Footer*/

  footer {
    margin-top: 5%;
    padding: 3%;
  }

  footer ul {
    width: 20%;
    margin-top: 2%;
  }
  footer p {
    margin-top: 1%;
  }

  footer ul li a:hover {
    font-weight: bold;
  }
  /* Portada */

  #portada main {
    margin: 0 auto;
    width: 70%;
    display: flex;
    flex-direction: row-reverse;
    justify-content: space-between;
    align-items: center;
    position: relative;
    text-align: center;
  }

  #portada div .subtitulo {
    position: relative;
    top: 0%;
    left: 0%;
    -webkit-transform: none;
    -ms-transform: none;
    transform: none;
    text-align: center;
  }
  #portada div p.subtitulo2 {
    width: 100%;
    margin-top: 5%;
  }

  /* Categoría*/

  #categoria main section ul li img {
    display: block;
  }
  #categoria main section ul li a {
    position: relative;
    left: 12%;
  }

  #categoria main section ul li a:hover .name_categoria {
    opacity: 1;
  }
  .name_categoria {
    position: absolute;
    right: 1px;
    left: 1px;
    top: 0;
    bottom: 0;
    height: 260px;
    width: 260px;
    opacity: 0;
    transition: 0.5s ease;
    background-color: var(--yellow-brazil);
    border-radius: 100%;
  }

  .name_categoria div {
    color: var(--blue-brazil);
    font-size: 1rem;
    position: absolute;
    top: 50%;
    left: 50%;
    -webkit-transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
    text-align: center;
  }

  /*  Detalle */

  #mandioca main p {
    margin-top: 2%;
  }

  #mandioca main h3 {
    margin-bottom: 2%;
    margin-top: 4%;
  }
  #mandioca main li {
    margin-bottom: 2%;
  }
  #mandioca iframe {
    height: 800px;
  }
  #mandioca main h2 {
    font-size: 3rem;
    margin-top: 2%;
    text-align: right;
  }
  #mandioca main div section:first-child p {
    width: 80%;
  }

  #mandioca main div section:nth-child(2n) div {
    width: 80%;
    margin: 0 auto;
  }

  /* Presentacion */

  #presentacion img {
    width: 40%;
  }

  #presentacion main h2 {
    margin-top: 2%;
    margin-bottom: 5%;
  }

  #presentacion main {
    width: 95%;
  }
  #presentacion main p {
    padding-bottom: 3%;
  }
  #presentacion main div {
    width: 80%;
    margin: 0 auto;
  }
  #presentacion div p:first-child {
    text-align: center;
    padding-bottom: 4%;
  }
  #presentacion main img {
    display: block;
    margin: auto;
    padding-bottom: 6%;
  }

  /* Enlaces */
  #enlaces main h2 {
    text-align: right;
    margin-bottom: 5%;
  }
  #enlaces main div {
    width: 80%;
    margin: 0 auto;
  }
}
